import { IconButton, Typography } from "@mui/material";
import { makeStyles } from "@mui/styles";

import LinkedInIcon from "@mui/icons-material/LinkedIn";
import GitHubIcon from "@mui/icons-material/GitHub";
import EmailIcon from "@mui/icons-material/Email";
import { height } from "@mui/system";

const useStyles = makeStyles(() => ({
  Container: {
    margin: " 2vh 2vw",
    width: "96vw",
    height: "84vh",

    display: "flex",
    justifyContent: "space-around",
    alignItems: "center",
  },
  Localizacao: {
    width: "70%",
    height: "80%",

    display: "flex",
    flexDirection: "column",
  },
  Contato: {
    display: "flex",
    flexDirection: "column",

    alignItems: "flex-start",

    border: "solid 4px #2129",
    borderRadius: "10px",

    boxShadow: "0.3rem 0.3rem 1px #112",

    backgroundColor: "#1129",

    width: "23vw",
    height: "84%",
  },
  Sociais: {
    display: "flex",
    flexDirection: "column",

    transform: "scale(1.05)",
    width: "auto",

    marginTop: "2rem",
    marginLeft: "3rem",
  },
  Destaque: {
    textAlign: "center",
    textDecoration: "none",
    marginBottom: "8rem",
    transition: "all 0.4s",
    backgroundColor: "white",
    borderRadius: "20px",
    boxShadow: "1rem 1rem 1rem gray",
    "&:hover": {
      transform: "scale(1.2)",
      boxShadow: "0 0 0.5rem white",
    },
    "&:active": {
      backgroundColor: "#fff8",
    },
  },
  Mapa: {
    height: "303px",
    marginBottom: "5rem",
    border: "4px solid white",
    borderRadius: "10px",
    boxShadow: "0 0 2rem purple",
    transition: "all 1s",
    backgroungColor: "whitesmoke",
  },
  Map: {
    width: "99.7%",
    height: "98.6%",
  },
  Info: {
    textShadow: "white 1px 0",
  },

  "@media (max-width: 900px)": {
    Localizacao: {
      display: "none",
    },
    Contato: {
      alignItems: "center",
      width: "90vw",
      height: "80vh",
    },
    Sociais: {
      marginTop: "8rem",
      marginLeft: "0",
    },
    Destaque: {
      marginBottom: "5rem",
    },
    Egg: {
      transform: "scale( 0.5 )",
    },
  },

  "@media (min-width: 900px)": {
    Egg: {
      display: "none",
    },
  },

  "@media (max-width: 600px)": {
    Egg: {
      display: "none",
    },
  },
}));

const Contact = () => {
  const styles = useStyles();
  return (
    <div className={styles.Container}>
      <div className={styles.Contato}>
        <div className={styles.Sociais}>
          <a
            className={styles.Destaque}
            href="https://github.com/CaioCesarP"
            target="_blank"
            title="CaioCesarP"
          >
            <IconButton>
              <label>GitHub</label>
              <GitHubIcon />
            </IconButton>
          </a>
          <a
            className={styles.Destaque}
            href="https://www.linkedin.com/in/profile-caio-c%C3%A9sar-link/"
            target="_blank"
            title="Caio César"
          >
            <IconButton color="info">
              <label>Linkedin</label>
              <LinkedInIcon />
            </IconButton>
          </a>
          <a
            className={styles.Destaque}
            href="mailto:caiocesarworks@gmail.com"
            target="_blank"
            title="caiocesarworks@gmail.com"
          >
            <IconButton color="secondary">
              <label>E-mail de contato</label>
              <EmailIcon />
            </IconButton>
          </a>
          <Typography variant="caption">
            Direitos reservados 2022 © Caio César
          </Typography>
          <div className={styles.Egg}>
            <img
              alt="Caio-pic"
              src="https://cdn.discordapp.com/attachments/524055688540848128/901635038041477151/media.io_sRfmmZmX.gif"
            />
          </div>
        </div>
      </div>
      <div className={styles.Localizacao}>
        <div className={styles.Card}>
          <div
            class="badge-base LI-profile-badge"
            data-locale="pt_BR"
            data-size="large"
            data-theme="dark"
            data-type="HORIZONTAL"
            data-vanity="profile-caio-césar-link"
            data-version="v1"
          >
            <a
              class="badge-base__link LI-simple-link"
              href="https://br.linkedin.com/in/profile-caio-c%C3%A9sar-link?trk=profile-badge"
            >
              Caio César Passos Viana Ponte
            </a>
          </div>
        </div>
        <div className={styles.Info}>
          <Typography gutterBottom align="center" variant="h4">
            Informações de contato ao lado
          </Typography>
          <Typography align="center" variant="h6">
            Todos os tipos de contato verificados diariamente e atualizados
            quando necessário
          </Typography>
        </div>
      </div>
      <script
        src="https://platform.linkedin.com/badges/js/profile.js"
        async
        defer
        type="text/javascript"
      ></script>
    </div>
  );
};

export default Contact;
